import { Text, View } from 'react-native'

import { Button } from '../../components/button/Button'
import { colorLight } from '../../config/styles'
import { SwitchScene, login, signup } from '../switch/Switch'
import { startStyles } from './StartStyles'

export const StartScreen = () => {
    return (
        <View style={startStyles.container}>
            <View style={{ flex: 1, justifyContent: 'flex-start', top: 253, gap: 21 }}>
                <Text
                    style={{
                        fontSize: 28,
                        color: colorLight,
                        fontWeight: 'bold',
                    }}
                >
                    Привіт, студенте!
                </Text>
                <Text
                    style={{
                        fontSize: 24,
                        color: colorLight,
                    }}
                >
                    Виберіть опцію, щоб продовжити
                </Text>
            </View>
            <View style={{ flex: 1, justifyContent: 'flex-end', gap: 30, bottom: 150 }}>
                <Button
                    onPress={() => {
                        SwitchScene(login)
                    }}
                >
                    Увійти
                </Button>
                <Button
                    onPress={() => {
                        SwitchScene(signup)
                    }}
                >
                    Зареєструватися
                </Button>
            </View>
        </View>
    )
}
